StructuredTextModel.Text:
CASE diStato OF
	0:	Q_sResult:='Triggerato M100';
		IF
			READ_MCODE_M100.Strobe
		THEN
			diStato:=1;
		END_IF;
	1:	Q_sResult:='Attesa Motion Program in Pausa';
		IF
			CncCsStatus[0].Sta.CsCtrlOutputs.ExecutingStatus=_eCNC_EXECUTING_STATE#_cncHold
		THEN
			diStato:=2;
		END_IF;
	2:	Q_sResult:='Pausa da ELiminare';
		IF
			DELAT_M100.Q
		THEN
			lrSyncPosX:=_CNC_Coord[0].AxActPos.X;
			diStato:=3;
		END_IF;
	3:
		IF
			SYNC_MOVE_X.InPosition
		THEN
			diStato:=4;
		END_IF;
	4:
		lrSyncPosX:=lrSyncPosX+0.01;
		IF
			lrSyncPosX>270.0
		THEN
			diStato:=5;
		END_IF;
	5:
		IF
			STOP_MOVE_X.Done
		THEN
			diStato:=9;
		END_IF;
	9:
		IF
			NOT READ_MCODE_M100.Strobe
		THEN
			diStato:=10;
		END_IF;
	10:
		IF
			CncCsStatus[0].Sta.CsCtrlOutputs.ExecutingStatus=_eCNC_EXECUTING_STATE#_cncExecuting
		THEN
			diStato:=0;
		END_IF;	
END_CASE;

G_xM100_FEED_HOLD:=diStato=1;
G_xM100_CYCLE_START:=diStato=10;
READ_MCODE_M100(
	Coord:=_CNC_Coord[0], 
	MCode:=sMCODE, 
	Enable:=TRUE, 
	MCodeNo:=100);
DELAT_M100(
	In:=diStato=2, 
	PT:=T#0s);
SYNC_MOVE_X(
	Coord:=_CNC_Coord[0], 
	Execute:=diStato=3, 
	LogicalMotorNo:=0, 
	Position:=lrSyncPosX 
	);
STOP_MOVE_X(
	Coord:=_CNC_Coord[0], 
	Execute:=diStato=5, 
	Deceleration:=alrDEC);
RESET_MCODE(
	Coord:=_CNC_Coord[0], 
	Execute:=diStato=9 
	);
