StructuredTextModel.Text:
CASE diStato OF
	0:
		IF
			READ_MCODE_M100.Strobe
		THEN
			diStato:=1;
		END_IF;
	1:
		IF
			CncCsStatus[0].Sta.CsCtrlOutputs.ExecutingStatus=_eCNC_EXECUTING_STATE#_cncHold
		THEN
			diStato:=2;
		END_IF;
	2:
		IF
			DELAT_M100.Q
		THEN
			diStato:=3;
		END_IF;
	3:
		IF
			MOVE_X.Done
		THEN
			diStato:=9;
		END_IF;
	9:
		IF
			NOT READ_MCODE_M100.Strobe
		THEN
			diStato:=10;
		END_IF;
	10:
		IF
			CncCsStatus[0].Sta.CsCtrlOutputs.ExecutingStatus=_eCNC_EXECUTING_STATE#_cncExecuting
		THEN
			diStato:=0;
			xGo:=FALSE;
		END_IF;	
END_CASE;

G_xM100_FEED_HOLD:=diStato=1;
G_xM100_CYCLE_START:=diStato=10;
READ_MCODE_M100(
	Coord:=_CNC_Coord[0], 
	MCode:=sMCODE, 
	Enable:=TRUE, 
	MCodeNo:=100);
DELAT_M100(
	In:=diStato=2, 
	PT:=T#2s);
MOVE_X(
	Coord:=_CNC_Coord[0], 
	Execute:=diStato=3, 
	LogicalMotorNo:=0, 
	Position:=280.0,
	Velocity:=20,
	Acceleration:=100,
	Jerk:=0,
	MoveMode:=_eCNC_MOVE_MODE#_cncAbsolute);
RESET_MCODE(
	Coord:=_CNC_Coord[0], 
	Execute:=diStato=9 
	);
